import React,{useState,useEffect} from 'react';
import './style/App.css';
import './style/Login.css'
import './style/Registrar.css'
import Login from './components/Login';
import Registrar from './components/Registrar';
import logo from './imagens/logo.png'


export default function App() {

  const [pagina,setPagina]=useState(0)

  useEffect(
    ()=>{
      const url=window.location.href
      const res=url.split('?')
      setPagina(res[1])
    }
  )


  const LinksPaginas=(pages)=>{
    if(pages==1){
      window.open('http://localhost:19006?1','_self')
    }else if(pages==2){
      window.open('http://localhost:19006?2','_self')
    }

  }

  const retornarPagina=()=>{
    if(pagina==1){
        return<Registrar/>
    }else if(pagina==2){
        return<Login/>
    }else{
      return <div>
                <button onClick={()=>LinksPaginas(1)}>Registrar</button>
                <button onClick={()=>LinksPaginas(2)}>Entrar</button>
             </div>
    }
  }

  return (
    <>
      {retornarPagina()}
    </>
  );
}
